import axios from 'axios';

const apiClient = axios.create({
  baseURL: 'http://localhost:3000/api/v1', // Rails APIのURL
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Bearer ${localStorage.getItem('token')}`,
  },
});

export default apiClient;
