import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';

const CalendarPage: React.FC = () => {
  const { isAuthenticated } = useAuth();
  const currentDate = new Date();
  const [year, setYear] = useState(currentDate.getFullYear());
  const [month, setMonth] = useState(currentDate.getMonth() + 1);
  const [selectedDay, setSelectedDay] = useState<number | null>(null);
  const [temperature, setTemperature] = useState<string>('');
  const [weight, setWeight] = useState<string>('');
  const [note, setNote] = useState<string>('');
  const [isPeriodStart, setIsPeriodStart] = useState<boolean>(false);
  const [isPeriodEnd, setIsPeriodEnd] = useState<boolean>(false);
  const [isDischarge, setIsDischarge] = useState<boolean>(false);
  const [isSpotting, setIsSpotting] = useState<boolean>(false);
  const [isTakingPill, setIsTakingPill] = useState<boolean>(false); // „Éî„É´ÊúçÁî®Áä∂ÊÖã„ÇíÁÆ°ÁêÜ
  const [selectedDates, setSelectedDates] = useState<number[]>([]);
  const [cycleLength, setCycleLength] = useState<number>(28); // „Éá„Éï„Ç©„É´„Éà„ÅÆÁîüÁêÜÂë®Êúü
  const [isYearMonthDropdownOpen, setIsYearMonthDropdownOpen] = useState(false);
  const announcements = [
    {
      title: "Â≠êÂÆÆÈ†∏„Åå„ÇìÊ§úË®∫„ÅÆÈ†ªÂ∫¶„Å´„Å§„ÅÑ„Å¶",
      content: "ÂÆöÊúüÁöÑ„Å™Ê§úË®∫„ÅåÊó©ÊúüÁô∫Ë¶ã„Å´„Å§„Å™„Åå„Çä„Åæ„Åô„ÄÇ20Ê≠≥‰ª•‰∏ä„ÅÆÊñπ„ÅØ2Âπ¥„Åî„Å®„Å´Ê§úË®∫„ÇíÂèó„Åë„Çã„Åì„Å®„ÅåÊé®Â•®„Åï„Çå„Åæ„Åô„ÄÇ",
      link: "https://naminamicl.jp/column/cervicalcanser/frequency-screening/",
      date: new Date("2024-11-02")
    },
    {
      title: "Â≠êÂÆÆÈ†∏„Åå„Çì„ÅÆÂÖÜÂÄô",
      content: "„Åä„Çä„ÇÇ„ÅÆ„ÅÆÂ¢óÂä†„ÄÅ‰∏çÊ≠£Âá∫Ë°Ä„ÄÅÁîüÁêÜÂë®Êúü‰ª•Â§ñ„ÅÆÂá∫Ë°Ä„Å™„Å©„ÅåË¶ã„Çâ„Çå„Åü„ÇâÂåªÁôÇÊ©üÈñ¢„Åß„ÅÆÊ§úÊüª„Çí„ÅäÂãß„ÇÅ„Åó„Åæ„Åô„ÄÇ",
      link: "https://ganjoho.jp/public/cancer/cervix_uteri/about.html",
      date: new Date("2024-11-02")
    },
  ];

  const latestAnnouncements = announcements
  .sort((a, b) => b.date.getTime() - a.date.getTime()) // Êó•‰ªò„ÅßÈôçÈ†Ü„ÇΩ„Éº„Éà
  .slice(0, 2); 

  const [selectedAnnouncement, setSelectedAnnouncement] = useState<null | { title: string; content: string; link: string }>(null);

  const handleAnnouncementClick = (announcement: { title: string; content: string; link: string }) => {
    setSelectedAnnouncement(announcement);
  };

  const handleCloseModal = () => {
    setSelectedAnnouncement(null);
  };

  // ÊõúÊó•„ÅÆÈÖçÂàó
  const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  // Êúà„ÅÆÊúÄÂàù„ÅÆÊõúÊó•„Å®Êó•Êï∞„ÇíË®àÁÆó
  const firstDayOfMonth = new Date(year, month - 1, 1).getDay();
  const daysInMonth = new Date(year, month, 0).getDate();

  // ÈÅ∏Êäû„Åï„Çå„ÅüÊó•‰ªò„ÅåÁâπÂÆö„ÅÆÊù°‰ª∂ÔºàÈÄ£Á∂ö„Åô„ÇãÊó•‰ªò„Åå7Êó•‰ª•‰∏äÔºâ„ÇíÊ∫Ä„Åü„Åô„Åã„ÉÅ„Çß„ÉÉ„ÇØ
  useEffect(() => {
    if (selectedDates.length >= 7) {
      const continuousDays = selectedDates.slice(-7);
      const isContinuous = continuousDays[6] - continuousDays[0] === 6;

      if (isContinuous) {
        const alertMessage = `${announcements[1].title}\n${announcements[1].content}\nË©≥Á¥∞„ÅØ„Åì„Å°„Çâ: ${announcements[1].link}`;
        alert(alertMessage);
      }
    }
  }, [selectedDates]);

  // Êó•‰ªò„Çí„ÇØ„É™„ÉÉ„ÇØ„Åó„Åü„Å®„Åç„ÅÆÂá¶ÁêÜ
  const handleDayClick = (day: number) => {
    setSelectedDay(day);
    setTemperature('');
    setWeight('');
    setNote('');
    setIsPeriodStart(false);
    setIsPeriodEnd(false);
    setIsDischarge(false);
    setIsSpotting(false);
    setIsTakingPill(false);

    // ÈÄ£Á∂öÊó•Êï∞„ÅÆ„ÉÅ„Çß„ÉÉ„ÇØÁî®„Å´ÈÅ∏Êäû„Åó„ÅüÊó•„Çí‰øùÂ≠ò
    setSelectedDates((prevDates) => {
      if (!prevDates.includes(day)) return [...prevDates, day];
      return prevDates;
    });
  };

  // Ë®òÈå≤„Çí‰øùÂ≠ò„Åô„ÇãÂá¶ÁêÜ
  const handleSaveRecord = () => {
    alert(`Êó•‰ªò: ${selectedDay} „ÅÆË®òÈå≤„Çí‰øùÂ≠ò„Åó„Åæ„Åó„Åü„ÄÇ\n‰ΩìÊ∏©: ${temperature}\n‰ΩìÈáç: ${weight}\n„Åä„Çä„ÇÇ„ÅÆ: ${isDischarge ? '„ÅÇ„Çä' : '„Å™„Åó'}\n‰∏çÊ≠£Âá∫Ë°Ä: ${isSpotting ? '„ÅÇ„Çä' : '„Å™„Åó'}\nËñ¨: ${isTakingPill ? 'ÊúçÁî®„ÅÇ„Çä' : 'ÊúçÁî®„Å™„Åó'}\n„É°„É¢: ${note}`);
    setSelectedDay(null);
  };

  // Âπ¥„Å®Êúà„ÅÆÂ§âÊõ¥Âá¶ÁêÜ
  const handleYearMonthChange = (newYear: number, newMonth: number) => {
    setYear(newYear);
    setMonth(newMonth);
    setIsYearMonthDropdownOpen(false);
  };

  return (
    <Layout>
      <div style={{ backgroundColor: '#f5f5f5', padding: '20px' }}>
        <h1 
          style={{ color: '#FF69B4', textAlign: 'center', cursor: 'pointer' }}
          onClick={() => setIsYearMonthDropdownOpen(!isYearMonthDropdownOpen)}
        >
          {year}Âπ¥ {month}Êúà
        </h1>

        {/* Âπ¥„Å®ÊúàÈÅ∏Êäû„ÅÆ„Éâ„É≠„ÉÉ„Éó„ÉÄ„Ç¶„É≥ */}
        {isYearMonthDropdownOpen && (
          <div style={{
            position: 'absolute',
            top: '50px',
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: '#fff',
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '10px',
            zIndex: 1000
          }}>
            <div>
              <h3 style={{ color: '#FF69B4' }}>Âπ¥„ÇíÈÅ∏Êäû</h3>
              {Array.from({ length: 5 }, (_, i) => year - 2 + i).map((y) => (
                <div
                  key={y}
                  onClick={() => handleYearMonthChange(y, month)}
                  style={{
                    padding: '5px 10px',
                    cursor: 'pointer',
                    backgroundColor: y === year ? '#FFEBE8' : '#fff',
                    color: y === year ? '#FF69B4' : '#000',
                  }}
                >
                  {y}Âπ¥
                </div>
              ))}
            </div>
            <div>
              <h3 style={{ color: '#FF69B4' }}>Êúà„ÇíÈÅ∏Êäû</h3>
              {Array.from({ length: 12 }, (_, i) => i + 1).map((m) => (
                <div
                  key={m}
                  onClick={() => handleYearMonthChange(year, m)}
                  style={{
                    padding: '5px 10px',
                    cursor: 'pointer',
                    backgroundColor: m === month ? '#FFEBE8' : '#fff',
                    color: m === month ? '#FF69B4' : '#000',
                  }}
                >
                  {m}Êúà
                </div>
              ))}
            </div>
          </div>
        )}

        {/* ÊõúÊó•Ë°®Á§∫ */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(7, 1fr)',
          gap: '5px',
          textAlign: 'center',
          marginTop: '10px',
          fontWeight: 'bold',
          color: '#666'
        }}>
          {weekdays.map((day) => (
            <div key={day}>{day}</div>
          ))}
        </div>

        {/* „Ç´„É¨„É≥„ÉÄ„Éº„ÅÆÊó•‰ªòË°®Á§∫ */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(7, 1fr)',
          gap: '5px',
          marginTop: '5px'
        }}>
          {/* Á©∫ÁôΩ„ÅÆ„Çª„É´„ÇíËøΩÂä†„Åó„Å¶„ÄÅÊúà„ÅÆÊúÄÂàù„ÅÆÊó•„ÅÆ‰ΩçÁΩÆ„ÇíË™øÊï¥ */}
          {Array.from({ length: firstDayOfMonth }).map((_, index) => (
            <div key={`empty-${index}`} />
          ))}

          {Array.from({ length: daysInMonth }, (_, i) => i + 1).map((day) => (
            <div
              key={day}
              onClick={() => handleDayClick(day)}
              style={{
                border: '1px solid #ddd',
                backgroundColor: '#ffffff',
                padding: '10px',
                borderRadius: '8px',
                textAlign: 'center',
                cursor: 'pointer'
              }}
            >
              {day}
            </div>
          ))}
        </div>

        {/* Ë®òÈå≤ÂÖ•Âäõ„Éï„Ç©„Éº„É† */}
        {selectedDay !== null && (
          <div style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: '#FFFFFF',
            padding: '20px',
            borderRadius: '10px',
            boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.3)',
            width: '90%',
            maxWidth: '340px',
            zIndex: 1000
          }}>
            <h2 style={{ color: '#FF69B4', textAlign: 'center' }}>{month}Êúà {selectedDay}Êó•„ÅÆË®òÈå≤</h2>

            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
              <label>
                <span style={{ color: '#555' }}>‰ΩìÊ∏©</span>
                <input
                  type="text"
                  value={temperature}
                  onChange={(e) => setTemperature(e.target.value)}
                  style={{
                    width: '80px',
                    padding: '5px',
                    margin: '5px',
                    border: '1px solid #ddd',
                    borderRadius: '4px'
                  }}
                  placeholder="¬∞C"
                />
              </label>

              <label>
                <span style={{ color: '#555' }}>‰ΩìÈáç</span>
                <input
                  type="text"
                  value={weight}
                  onChange={(e) => setWeight(e.target.value)}
                  style={{
                    width: '80px',
                    padding: '5px',
                    margin: '5px',
                    border: '1px solid #ddd',
                    borderRadius: '4px'
                  }}
                  placeholder="kg"
                />
              </label>
            </div>

            {/* „Éú„Çø„É≥„ÅÆÈÖçÁΩÆÔºàÈñãÂßã„ÉªÁµÇ‰∫Ü„Éª„Åä„Çä„ÇÇ„ÅÆ„Éª‰∏çÊ≠£Âá∫Ë°ÄÔºâ */}
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '10px' }}>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <button
                  onClick={() => setIsDischarge(!isDischarge)}
                  style={{
                    backgroundColor: isDischarge ? '#FF69B4' : '#ddd',
                    color: isDischarge ? '#FFFFFF' : '#555',
                    padding: '10px',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginRight: '14px',
                  }}
                >
                  üíß
                </button>
                <button
                  onClick={() => setIsSpotting(!isSpotting)}
                  style={{
                    backgroundColor: isSpotting ? '#FF69B4' : '#ddd',
                    color: isSpotting ? '#FFFFFF' : '#555',
                    padding: '10px',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginRight: '10px'
                  }}
                >
                  ü©∏
                </button>
              </div>
              <button
                  onClick={() => setIsTakingPill(!isTakingPill)}
                  style={{
                    backgroundColor: isTakingPill ? '#FF69B4' : '#ddd',
                    color: isTakingPill ? '#FFFFFF' : '#555',
                    padding: '10px',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginRight: '60px'
                  }}
                >
                  üíä
                </button>
              <span style={{ fontSize: '1.5rem', marginRight: '10px' }}>üåô</span>
              <button
                onClick={() => { setIsPeriodStart(true); setIsPeriodEnd(false); }}
                style={{
                  backgroundColor: isPeriodStart ? '#FF69B4' : '#ddd',
                  color: isPeriodStart ? '#FFFFFF' : '#555',
                  padding: '10px',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  marginRight: '10px'
                }}
              >
                ÈñãÂßã
              </button>

              <button
                onClick={() => { setIsPeriodStart(false); setIsPeriodEnd(true); }}
                style={{
                  backgroundColor: isPeriodEnd ? '#FF69B4' : '#ddd',
                  color: isPeriodEnd ? '#FFFFFF' : '#555',
                  padding: '10px',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                ÁµÇ‰∫Ü
              </button>
            </div>

            <label style={{ display: 'block', marginBottom: '10px' }}>
              <span style={{ color: '#555' }}>„É°„É¢</span>
              <textarea
                value={note}
                onChange={(e) => setNote(e.target.value)}
                style={{
                  width: '100%',
                  height: '60px',
                  padding: '5px',
                  border: '1px solid #ddd',
                  borderRadius: '4px'
                }}
                placeholder="„É°„É¢„ÇíÂÖ•Âäõ"
              />
            </label>

            <button
              onClick={handleSaveRecord}
              style={{
                backgroundColor: '#FF69B4',
                color: '#FFFFFF',
                padding: '10px',
                width: '100%',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                marginBottom: '10px'
              }}
            >
              ‰øùÂ≠ò
            </button>

            <button
              onClick={() => setSelectedDay(null)}
              style={{
                backgroundColor: '#ddd',
                color: '#555',
                padding: '10px',
                width: '100%',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              „Ç≠„É£„É≥„Çª„É´
            </button>
          </div>
        )}

        {/* ËÉåÊôØ„ÅÆÊöó„ÅÑ„Ç™„Éº„Éê„Éº„É¨„Ç§ */}
        {selectedDay !== null && (
          <div
            onClick={() => setSelectedDay(null)}
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              zIndex: 999
            }}
          />
        )}
        {/* ‰Ωø„ÅÑÊñπ„Éö„Éº„Ç∏„Å∏„ÅÆ„É™„É≥„ÇØ„ÇíËøΩÂä† */}
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <Link to="/usageguide" style={{ color: '#FF69B4', textDecoration: 'none', fontWeight: 'bold' }}>
            ‚Äª‰Ωø„ÅÑÊñπ
          </Link>
        </div>

        {/* ÊúÄÊñ∞„Ç¢„Éä„Ç¶„É≥„Çπ„ÅÆË°®Á§∫ */}
        <div style={{ marginTop: '40px', textAlign: 'center' }}>
          <h3 style={{ color: '#FF69B4' }}>ÊúÄÊñ∞„ÅÆ„ÅäÁü•„Çâ„Åõ</h3>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {latestAnnouncements.map((announcement, index) => (
              <li 
                key={index} 
                style={{ margin: '10px 0', padding: '10px', backgroundColor: '#ffffff', borderRadius: '4px', boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)', cursor: 'pointer' }}
                onClick={() => handleAnnouncementClick(announcement)}
              >
                <h4 style={{ color: '#FF69B4' }}>{announcement.title}</h4>
                <a href="#" style={{ color: '#FF69B4', textDecoration: 'none' }}>Ë©≥Á¥∞„ÅØ„Åì„Å°„Çâ</a>
              </li>
            ))}
          </ul>
        </div>

        {/* „É¢„Éº„ÉÄ„É´„Éù„ÉÉ„Éó„Ç¢„ÉÉ„Éó */}
        {selectedAnnouncement && (
          <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000
          }}
            onClick={handleCloseModal}
          >
            <div 
              style={{
                backgroundColor: '#fff',
                padding: '20px',
                maxWidth: '350px',
                width: '90%',
                borderRadius: '8px',
                boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                position: 'relative'
              }}
              onClick={(e) => e.stopPropagation()} // „É¢„Éº„ÉÄ„É´Â§ñ„Çí„ÇØ„É™„ÉÉ„ÇØ„Åó„Å¶Èñâ„Åò„Çã
            >
              <h3 style={{ color: '#FF69B4', marginBottom: '10px' }}>{selectedAnnouncement.title}</h3>
              <p>{selectedAnnouncement.content}</p>
              <a href={selectedAnnouncement.link} target="_blank" rel="noopener noreferrer" style={{ color: '#FF69B4' }}>
                Ë©≥Á¥∞„ÅØ„Åì„Å°„Çâ
              </a>
              <button 
                onClick={handleCloseModal} 
                style={{
                  position: 'absolute',
                  top: '10px',
                  right: '10px',
                  background: 'none',
                  border: 'none',
                  fontSize: '1.5rem',
                  color: '#999',
                  cursor: 'pointer'
                }}
              >
                √ó
              </button>
            </div>
          </div>
        )}      </div>
    </Layout>
  );
};

export default CalendarPage;
