  useEffect(() => {
    if (isPeriodEnd) {
      setIsPeriodStart(false);
    }
  }, [isPeriodEnd]);

  // é¸æŠžã•ã‚ŒãŸæ—¥ä»˜ãŒç‰¹å®šã®æ¡ä»¶ï¼ˆé€£ç¶šã™ã‚‹æ—¥ä»˜ãŒ7æ—¥ä»¥ä¸Šï¼‰ã‚’æº€ãŸã™ã‹ãƒã‚§ãƒƒã‚¯
  useEffect(() => {
    console.log("fetchRecordsã‚’å®Ÿè¡Œ");
    const fetchRecords = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/v1/records');
        console.log("å–å¾—ã—ãŸãƒ‡ãƒ¼ã‚¿:", response.data);
        const fetchedRecords = response.data.reduce((acc: any, record: any) => {
          acc[record.record_date] = record;
          return acc;
        }, {});
        setRecords(fetchedRecords);
