import React, { useState, useEffect, useMemo } from 'react';
import Layout from '../components/Layout';
import { useAuth } from '../context/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import helpIcon from '../images/help_icon.png';
import axios, { AxiosError } from 'axios';

const CalendarPage: React.FC = () => {
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate(); 
  const currentDate = new Date();
  const [year, setYear] = useState(currentDate.getFullYear());
  const [month, setMonth] = useState(currentDate.getMonth() + 1);
  const [selectedDay, setSelectedDay] = useState<number | null>(null);
  const [temperature, setTemperature] = useState<string>('');
  const [weight, setWeight] = useState<string>('');
  const [note, setNote] = useState<string>('');
  const [isPeriodStart, setIsPeriodStart] = useState<boolean>(false);
  const [isPeriodEnd, setIsPeriodEnd] = useState<boolean>(false);
  const [isDischarge, setIsDischarge] = useState<boolean>(false);
  const [isSpotting, setIsSpotting] = useState<boolean>(false);
  const [isTakingPill, setIsTakingPill] = useState<boolean>(false);
  const [selectedDates, setSelectedDates] = useState<number[]>([]);
  const [cycleLength, setCycleLength] = useState<number>(28);
  const [isYearMonthDropdownOpen, setIsYearMonthDropdownOpen] = useState(false);
  const [isHelpModalOpen, setIsHelpModalOpen] = useState<boolean>(false);
  const [showHelpPopup, setShowHelpPopup] = useState(false);
  const [records, setRecords] = useState<{ [key: string]: any }>({});
  const [showPeriodIcon, setShowPeriodIcon] = useState<boolean>(false); 

  const announcements = [
    {
      title: "Â≠êÂÆÆÈ†∏„Åå„ÇìÊ§úË®∫„ÅÆÈ†ªÂ∫¶„Å´„Å§„ÅÑ„Å¶",
      content: "ÂÆöÊúüÁöÑ„Å™Ê§úË®∫„ÅåÊó©ÊúüÁô∫Ë¶ã„Å´„Å§„Å™„Åå„Çä„Åæ„Åô„ÄÇ20Ê≠≥‰ª•‰∏ä„ÅÆÊñπ„ÅØ2Âπ¥„Åî„Å®„Å´Ê§úË®∫„ÇíÂèó„Åë„Çã„Åì„Å®„ÅåÊé®Â•®„Åï„Çå„Åæ„Åô„ÄÇ",
      link: "https://naminamicl.jp/column/cervicalcanser/frequency-screening/",
      date: new Date("2024-11-02")
    },
    {
      title: "Â≠êÂÆÆÈ†∏„Åå„Çì„ÅÆÂÖÜÂÄô",
      content: "„Åä„Çä„ÇÇ„ÅÆ„ÅÆÂ¢óÂä†„ÄÅ‰∏çÊ≠£Âá∫Ë°Ä„ÄÅÁîüÁêÜÂë®Êúü‰ª•Â§ñ„ÅÆÂá∫Ë°Ä„Å™„Å©„ÅåË¶ã„Çâ„Çå„Åü„ÇâÂåªÁôÇÊ©üÈñ¢„Åß„ÅÆÊ§úÊüª„Çí„ÅäÂãß„ÇÅ„Åó„Åæ„Åô„ÄÇ",
      link: "https://ganjoho.jp/public/cancer/cervix_uteri/about.html",
      date: new Date("2024-11-02")
    },
  ];

  const latestAnnouncements = announcements
  .sort((a, b) => b.date.getTime() - a.date.getTime()) 
  .slice(0, 2); 

  const [selectedAnnouncement, setSelectedAnnouncement] = useState<null | { title: string; content: string; link: string }>(null);

  const handleAnnouncementClick = (announcement: { title: string; content: string; link: string }) => {
    setSelectedAnnouncement(announcement);
  };

  const handleCloseModal = () => {
    setSelectedAnnouncement(null);
  };

  // ÊõúÊó•„ÅÆÈÖçÂàó
  const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  // Êúà„ÅÆÊúÄÂàù„ÅÆÊõúÊó•„Å®Êó•Êï∞„ÇíË®àÁÆó
  const firstDayOfMonth = new Date(year, month - 1, 1).getDay();
  const daysInMonth = new Date(year, month, 0).getDate();

  const fetchRecords = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/v1/records');
      console.log("Fetched raw data from API:", response.data); // API„É¨„Çπ„Éù„É≥„Çπ„Çí„É≠„Ç∞„Å´Âá∫Âäõ
      const fetchedRecords = response.data.reduce((acc: any, record: any) => {
        const formattedDate = new Date(record.record_date).toISOString().split('T')[0]; // YYYY-MM-DDÂΩ¢Âºè„Å´Â§âÊèõ
        acc[formattedDate] = record;
        return acc;
      }, {});

      // „Éá„Éê„ÉÉ„Ç∞„É≠„Ç∞
      console.log("Fetched raw data from API:", response.data);
      console.log("Formatted records:", fetchedRecords);

      setRecords(fetchedRecords);
    } catch (error) {
      console.error('Error fetching records:', error);
    }
  };

  // ‰∏ãË®ò„ÅÆÊßãÈÄ†„Å´„Å§„ÅÑ„Å¶„ÄÅ„Éà„É™„Ç¨„Éº„ÅÆÂ§âÊï∞„ÅåË™≠„ÅøËæº„Åæ„Çå„Åü„Çä„ÄÅÂÄ§„ÅåÊõ∏„ÅçÊèõ„Çè„Å£„ÅüÂ†¥Âêà„Å´Âá¶ÁêÜ„ÅåËµ∞„Çã
  // useEffect(()=>{Âá¶ÁêÜ„ÇíË®òËø∞},[„Éà„É™„Ç¨„Éº„Å´„Å™„ÇãÂ§âÊï∞„ÇíË®òËø∞])
  useEffect(() => {
    if (isPeriodStart || isPeriodEnd) {
      setShowPeriodIcon(true);
      if (isPeriodEnd) {
        setIsPeriodStart(false);
      }
    } else {
      setShowPeriodIcon(false);
    }
  }, [isPeriodStart, isPeriodEnd]);


  useEffect(() => {
    fetchRecords();
  }, []);

  useEffect(() => {
    if (Object.keys(records).length > 0) {
      console.log("Records updated:", records);
    }
  }, [records]);

  useEffect(() => {
    const validatePeriodDates = () => {
      const startDates = Object.keys(records)
        .filter((key) => records[key]?.is_period_start)
        .sort((a, b) => new Date(a).getTime() - new Date(b).getTime());
      const endDates = Object.keys(records)
        .filter((key) => records[key]?.is_period_end)
        .sort((a, b) => new Date(a).getTime() - new Date(b).getTime());

      if (startDates.length > 0 && endDates.length > 0) {
        const lastStart = new Date(startDates[startDates.length - 1]);
        const lastEnd = new Date(endDates[endDates.length - 1]);

        if (lastStart > lastEnd) {
          console.error("ÁîüÁêÜÈñãÂßãÊó•„ÅåÁµÇ‰∫ÜÊó•„Çà„ÇäÂæå„Å´„Å™„Å£„Å¶„ÅÑ„Åæ„Åô„ÄÇ„Éá„Éº„Çø„ÇíÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ");
        }
      }
    };

    validatePeriodDates();
  }, [records]);


  // Êó•‰ªò„Çí„ÇØ„É™„ÉÉ„ÇØ„Åó„Åü„Å®„Åç„ÅÆÂá¶ÁêÜ
  const handleDayClick = (day: number) => {
    setSelectedDay(day);
    const dateKey = `${year}-${month}-${day}`;
    const record = records[dateKey];

    setTemperature(record?.temperature ?? '');
    setWeight(record?.weight ?? '');
    setNote(record?.note ?? '');
    setIsPeriodStart(record?.is_period_start ?? false);
    setIsPeriodEnd(record?.is_period_end ?? false);
    setIsDischarge(record?.is_discharge ?? false);
    setIsSpotting(record?.is_spotting ?? false);
    setIsTakingPill(record?.is_taking_pill ?? false);
  };

  // „Éú„Çø„É≥„ÅÆÂà∂Âæ°„É≠„Ç∏„ÉÉ„ÇØ
  const isPeriodStartDisabled = isPeriodEnd || Object.values(records).some(
    (record: any) => record.is_period_start
  );
  const isPeriodEndDisabled = !isPeriodStart || isPeriodEnd || !Object.values(records).some(
    (record: any) => record.is_period_start
  );

    // ÈÄ£Á∂öÊó•Êï∞„ÅÆ„ÉÅ„Çß„ÉÉ„ÇØÁî®„Å´ÈÅ∏Êäû„Åó„ÅüÊó•„Çí‰øùÂ≠ò
  //   setSelectedDates((prevDates) => {
  //     if (!prevDates.includes(day)) return [...prevDates, day];
  //     return prevDates;
  //   });
  // };

  // Âπ¥„Å®Êúà„ÅÆÂ§âÊõ¥Âá¶ÁêÜ
  const handleYearMonthChange = (newYear: number, newMonth: number) => {
    setYear(newYear);
    setMonth(newMonth);
    setIsYearMonthDropdownOpen(false);
  };

  const handleHelpClick = () => {
    setShowHelpPopup(true);
  };

  const handleCloseHelpPopup = () => {
    setShowHelpPopup(false);
  };

  // Ë®òÈå≤„Çí‰øùÂ≠ò„Åô„ÇãÂá¶ÁêÜ

  const handleSaveRecord = async () => {
    if (!selectedDay) return;

    // „É¶„Éº„Ç∂„ÉºID„ÇíÂèñÂæó
    const userId = isAuthenticated ? 1 : null;
    if (!userId) {
      console.error("„É¶„Éº„Ç∂„ÉºID„ÅåË®≠ÂÆö„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì");
      return;
    }

    const recordDate = `${year}-${month.toString().padStart(2, '0')}-${selectedDay.toString().padStart(2, '0')}`;
    const recordData = {
      record: {
        user_id: userId,
        record_date: recordDate,
        temperature: temperature ? parseFloat(temperature) : null,
        weight: weight ? parseFloat(weight) : null,
        note: note,
        is_period_start: isPeriodStart,
        is_period_end: isPeriodEnd,
        is_discharge: isDischarge,
        is_spotting: isSpotting,
        is_taking_pill: isTakingPill,
      },
    };

    try {
      // „Åæ„Åö„ÅØÂêå„ÅòÊó•‰ªò„ÅÆË®òÈå≤„Åå„ÅÇ„Çã„Åã„Å©„ÅÜ„ÅãÁ¢∫Ë™ç
      const response = await axios.get(`http://localhost:3001/api/v1/records?record_date=${recordDate}&user_id=${userId}`);

      if (response.data && response.data.id) {
        // Ë®òÈå≤„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅØÊõ¥Êñ∞
        await axios.put(`http://localhost:3001/api/v1/records/${response.data.id}`, recordData);
      } else {
        // Ë®òÈå≤„Åå„Å™„ÅÑÂ†¥Âêà„ÅØÊñ∞Ë¶è‰ΩúÊàê
        await axios.post('http://localhost:3001/api/v1/records/create_or_update', recordData);
        console.log("Record saved:", response.data);
      }

      // ‰øùÂ≠òÂÆå‰∫ÜÂæå„ÄÅË®òÈå≤„ÇíÊõ¥Êñ∞
      setRecords((prevRecords) => ({
        ...prevRecords,
        [recordDate]: recordData.record,
      }));

      await fetchRecords();

      // ÈÅ∏Êäû„Åï„Çå„ÅüÊó•‰ªò„ÇíËß£Èô§„Åó„Å¶„Éï„Ç©„Éº„É†„ÇíÈñâ„Åò„Çã
      setSelectedDay(null);
    } catch (error) {
      const axiosError = error as AxiosError; // error „Çí AxiosError Âûã„Å´„Ç≠„É£„Çπ„Éà
      if (axiosError.response && axiosError.response.status === 404) {
        await axios.post('http://localhost:3001/api/v1/records', recordData);
      } else {
        console.error('Error saving record:', axiosError.response?.data || axiosError.message);
        alert('Ë®òÈå≤„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
        return;
      }
    }
    navigate('/');
  };

  // üåô„Éû„Éº„ÇØ„ÇíÁîüÁêÜÊúüÈñì‰∏≠„Å´Ë°®Á§∫„Åô„ÇãÂá¶ÁêÜ
  const getPeriodIcon = (day: number) => {
    const dateKey = `${year}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;
    console.log("Checking date:", dateKey);
    const record = records[dateKey];

    console.log("Record data for the date:", record);

    if (record?.is_period_start) {
      console.log("üåô Period start for:", dateKey);
      return 'üåô'; // ÁîüÁêÜÈñãÂßãÊó•
    }

    if (record?.is_period_end) {
      console.log("üåô Period end for:", dateKey);
      return 'üåô'; // ÁîüÁêÜÁµÇ‰∫ÜÊó•
    }

    // ÁîüÁêÜÊúüÈñì‰∏≠„ÅÆ„Ç¢„Ç§„Ç≥„É≥Ë°®Á§∫
    const startDates = Object.keys(records)
      .filter((key) => records[key]?.is_period_start)
      .sort((a, b) => new Date(a).getTime() - new Date(b).getTime());
    const endDates = Object.keys(records)
      .filter((key) => records[key]?.is_period_end)
      .sort((a, b) => new Date(a).getTime() - new Date(b).getTime());

    // „Éá„Éê„ÉÉ„Ç∞„É≠„Ç∞
    console.log("Start dates:", startDates);
    console.log("End dates:", endDates);

    if (startDates.length > 0) {
      const lastStart = new Date(startDates[startDates.length - 1]);
      const lastEnd = endDates.length > 0 ? new Date(endDates[endDates.length - 1]) : null;
      const currentDate = new Date(dateKey);

      // „Éá„Éê„ÉÉ„Ç∞„É≠„Ç∞
      console.log("Last start date:", lastStart);
      console.log("Last end date:", lastEnd);
      console.log("Current date being checked:", currentDate);

      if (lastStart <= currentDate && (!lastEnd || currentDate <= lastEnd)) {
        console.log("üåô Icon for:", dateKey);
        return 'üåô';
      }
    }

    return null;
  };




const toggleIsPeriodEnd = () => {
  setIsPeriodEnd((prev) => !prev);
  if (!isPeriodEnd) {
    setIsPeriodStart(false);
  }
};

const calendarDays = useMemo(() => {
  const days = Array.from({ length: daysInMonth }, (_, i) => i + 1);
  return days.map((day) => {
    const dateKey = `${year}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;
    const record = records[dateKey];

    // „Éá„Éê„ÉÉ„Ç∞„É≠„Ç∞
    console.log("Day being rendered:", day);
    console.log("Date key:", dateKey);
    console.log("Record for the day:", record);

  return (
    <div
      key={day}
      className="calendar-day"
      style={{
        border: '1px solid #ddd',
        backgroundColor: selectedDay === day ? '#FFE4E1' : '#ffffff',
        padding: '10px',
        borderRadius: '8px',
        textAlign: 'center',
        cursor: 'pointer',
        position: 'relative',
      }}
      onClick={() => handleDayClick(day)} // Êó•‰ªò„Çí„ÇØ„É™„ÉÉ„ÇØ„Åó„Åü„Å®„Åç„ÅÆ„Ç§„Éô„É≥„Éà
    >
      {day}
      {/* „É¨„Ç≥„Éº„Éâ„Å´Âü∫„Å•„ÅÑ„Å¶ËøΩÂä†„ÅÆÊÉÖÂ†±„ÇíË°®Á§∫ */}
      {record && (
        <div style={{ fontSize: '0.3em', color: '#555', marginTop: '5px' }}>
          {/* {record.is_period_start && 'üåô'}
          {record.is_period_end && 'üåô'} */}
          {record.is_discharge && 'üíß'}
          {record.is_spotting && 'ü©∏'}
          {record.is_taking_pill && 'üíä'}
          {getPeriodIcon(day)}
        </div>
      )}
    </div>
  );
});}, [year, month, daysInMonth, records]);

  return (
    <Layout>
      {/* <div className="calendar">{renderCalendar()}</div> */}
      <div style={{ backgroundColor: '#f5f5f5', padding: '20px' }}>
        <h1 
          style={{ color: '#FF69B4', textAlign: 'center', cursor: 'pointer' }}
          onClick={() => setIsYearMonthDropdownOpen(!isYearMonthDropdownOpen)}
        >
          {year}Âπ¥ {month}Êúà
        </h1>

        {/* Âπ¥„Å®ÊúàÈÅ∏Êäû„ÅÆ„Éâ„É≠„ÉÉ„Éó„ÉÄ„Ç¶„É≥ */}
        {isYearMonthDropdownOpen && (
          <div style={{
            position: 'absolute',
            top: '50px',
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: '#fff',
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '10px',
            zIndex: 1000
          }}>
            <div>
              <h3 style={{ color: '#FF69B4' }}>Âπ¥„ÇíÈÅ∏Êäû</h3>
              {Array.from({ length: 5 }, (_, i) => year - 2 + i).map((y) => (
                <div
                  key={y}
                  onClick={() => handleYearMonthChange(y, month)}
                  style={{
                    padding: '5px 10px',
                    cursor: 'pointer',
                    backgroundColor: y === year ? '#FFEBE8' : '#fff',
                    color: y === year ? '#FF69B4' : '#000',
                  }}
                >
